INSERT INTO Wines VALUES(2150, "Home Place Red Wine", "Red Blend", "The Grande Dalles", "Red", 2008, "Columbia Valley - OR", 0, 0, "", "An aromatic, incredibly distinctive wine with freshly ground tobacco, orange peel, dark cherries, licorice, and light herbal tones. The palate is absolutely loaded with tobacco flavors and a wall of rustic tannins that take a full day of being open to begin to resolve. Needs food to help temper the tannins but delivers a lot of enjoyment and is among the more unique wines I?????????ve come across in the Northwest. 70% Tempranillo, 28% Cabernet Sauvignon, 2% Cabernet Franc. Rock Flower Hill Vineyard. Aged 16 months in French oak. 13.5% alcohol. 115 cases produced. Sample provided by winery.", 4.0, "", "", 0.14962614746014372, 0.08294429072493838, 0.29641180560526964, 0.0, 0.0, 0.6889114277092357, 0.2588737319554151, 0.0, 0.28990201161626056, 0.19200509048233644, 0.2704144723404324, 0.39098371382016406, 0.0, 0.0, 0.5957551368884317, 0.0);
