INSERT INTO Wines VALUES(2082, "Cabernet Sauvignon", "Cabernet Sauvignon", "Gramercy Cellars", "Red", 2009, "Columbia Valley - WA", 0, 0, "", "Aromatically generous with high toned herbal notes, floral notes, and restrained cherry aromas. The palate is ripe and full of incredibly persistent cherry flavors that linger on the finish. A superb, standout Cabernet Sauvignon. 77% Cabernet Sauvignon, 14% Cabernet Franc, 9% Merlot. Phinny Hill, Portteus, Estate, Pepper Bridge, and Octave vineyards. Aged in French oak (40% new). 14.2% alcohol. 540 cases produced.", 5.0, "", "", 0.0, 0.0, 0.2244271525576628, 0.0, 0.0, 0.7287010266418302, 0.0, 0.0, 0.41280183769895695, 0.2169343288296383, 0.22788556798685428, 0.4245903402485726, 0.0, 0.0, 0.0, 0.0);
