INSERT INTO Wines VALUES(1967, "Sauvignon Blanc Boushey Vineyard", "Sauvignon Blanc", "Avennia", "White", 2011, "Yakima Valley - WA", 0, 0, "", "An aromatically intriguing wine with smoke, toast, roasted nuts, citrus, and a complex medley of grass and herbal notes. The palate in medium bodied with a creamy feel, starting out broadly and then pulling back and showing a sense of lightness and elegance on the back half accented by tart, lemony acidity throughout. A style that will fit in best at the dinner table rather than sitting and sipping. 100% Sauvignon Blanc. Barrel fermented in French oak (10% new) and aged sur lies for eight months. 225 cases produced. Sample provided by winery", 3.5, "", "", 0.17570820102092796, 0.15277157851910206, 0.7515328913367826, 0.0, 0.0, 0.0, 0.0, 0.15768515014837065, 0.0, 0.0, 0.12745192976891942, 0.42363981142859025, 0.6603449074074074, 0.0, 0.3298212448559671, 0.20321436108473143);
