INSERT INTO Wines VALUES(1953, "Liberte Syrah Boushey Vineyard", "Syrah", "Maison Bleue", "Red", 2010, "Yakima Valley - WA", 0, 0, "", "An aromatically complex wine with dark plum, mineral, huckleberry, currant, floral notes, licorice, and orange peel. The palate is arresting with rich, incredibly concentrated, pure, textured flavors of pomegranate, plum, and pepper, and a finish that persists for nearly a minute. This is a spectacular, mouthful of a wine that only ramps up in intensity as it opens up in the glass and will aged for as long as you care to keep it. As good as the winery has made to date and one of the best wines I?????????ve had this year. 100% Syrah. Fermented with approximately 30% whole berries. Aged 10 months in one to two year old French oak. 14.5% alcohol. 200 cases produced. Sample provided by winery.", 5.0, "", "", 0.49986675002426334, 0.2942394795236197, 0.402233142231408, 0.0, 0.0, 0.0, 0.8900393109307552, 0.0, 0.3882942302321397, 0.34925559121200006, 0.21509102416850148, 0.0, 0.0, 0.3060742159060129, 0.0, 0.22471220643831222);
INSERT INTO Wines VALUES(1952, "Le Midi Grenache Boushey Vineyard", "Grenache", "Maison Bleue", "Red", 2010, "Yakima Valley - WA", 0, 0, "", "An aromatic dazzler that boasts pure black and red raspberries, white pepper, garrigue, anise, currant, and mineral notes ????????? this is an aroma profile to spend some time lingering over. Broadens out and expands across the palate with the purest flavors of kirsch and licorice with an herbal accent. Lingers on the finish. The intensity of fruit here is breathtaking with this wine raising the bar for Washington Grenache to new heights. 100% Grenache. Fermented with approximately 30% whole berries. Aged 10 months in 3-4 year old French oak. 14.5% alcohol. 200 cases produced. Sample provided by winery.", 5.0, "", "", 0.26912115661814906, 0.13188280737557462, 0.270287619484026, 0.0, 0.0, 0.0, 0.8256358685169544, 0.0, 0.0, 0.4493861196882811, 0.21637918636550618, 0.36001843427585994, 0.0, 0.0, 0.0, 0.19954407161197282);
