INSERT INTO Wines VALUES(1637, "Viognier", "Viognier", "Ott & Murphy", "White", 2010, "Columbia Valley - WA", 0, 0, "", "Pale lemon yellow. A moderately aromatic wine full of peach, creamsicle, and floral notes. The palate is crisp, tart and clean with a creamy feel without getting overweighted, carried along by lime-like acidity. 77% Viognier, 20% Roussanne, and 3% Marsanne. Sugarloaf, Elephant Mountain, Coyote Canyon, and Boushey vineyards. Fermented and aged in neutral French oak. 13.3% alcohol. 125 cases produced. Sample provided by winery.", 3.5, "", "", 0.20950594457510108, 0.4837183525666738, 0.7116240949970685, 0.0, 0.0, 0.74894167877601, 0.0, 0.30006189263866645, 0.42547755133146076, 0.0, 0.31384896004293694, 0.0, 0.5134118312757202, 0.0, 0.0, 0.0);
