INSERT INTO Wines VALUES(1506, "Pinot Gris", "Pinot Gris", "Milbrandt Traditions", "White", 2009, "Columbia Valley - WA", 0, 0, "", "Pale in color. Aromatically appealing with straw, apple notes, and light lemon zest. The palate has a full, rounded feel with red and green apple flavors accented by a citric finish. 100% Pinot Gris. Evergreen Vineyard. 12.5% alcohol.", 3.5, "", "", 0.0, 0.25017843603481194, 0.5391201206049487, 0.0, 0.0, 0.6915763723722657, 0.0, 0.0, 0.0, 0.09564994867312539, 0.2723426752737414, 0.591452568263166, 0.0, 0.0, 0.0, 0.20848084324035965);
