INSERT INTO Wines VALUES(1318, "Merlot", "Merlot", "Laurelhurst Cellars", "Red", 2008, "Columbia Valley - WA", 0, 0, "", "A slight brick red tinge. Leaps up from the glass with abundant ripe red fruit aromas of raspberry, bing cherry, and red currant along with coffee, espresso bean, and a light herbal undertone. Palate is tart and full of ripe fruit flavors, chocolate, and soft but structured tannins. Lingers on the finish. A compelling wine at an even more compelling price point. Boushey and Klipsun vineyards. 14.6% alcohol. 67 cases produced. Sample provided by winery.", 4.0, "", "", 0.5389421375272302, 0.2475424163660256, 0.4454197063435071, 0.4441669973478209, 0.0, 0.68680921507465, 0.0, 0.0, 0.0, 0.0926631982735382, 0.6622653659320383, 0.46849537553229786, 0.3232043735532407, 0.5547567624230567, 0.17955798530735595, 0.0);
