INSERT INTO Wines VALUES(1305, "Syrah", "Syrah", "Gramercy Cellars", "Red", 2009, "Walla Walla Valley - WA", 0, 0, "", "An intensely aromatic wine with violets, smoked meat, light chocolate, and mineral notes. The palate is textured with layers of complexity crisply defined by acidity. An exclamation point of Walla Walla Valley Syrah. 100% Syrah. 95% Les Collines Vineyard, 5% Minick Vineyard. Aged 18 months in 500L and 225L neutral barrels. 13.9% alcohol. 605 cases produced.", 5.0, "", "", 0.2493795315869902, 0.06903084962355042, 0.386251919452939, 0.3749147295358533, 0.0, 0.0, 0.21544911055256769, 0.0, 0.17616271474296163, 0.0, 0.0, 0.0, 0.14776234567901234, 0.25362330217175216, 0.2569873113854595, 0.08419506876296999);
