INSERT INTO Wines VALUES(824, "Syrah", "Syrah", "Lantz Cellars", "Red", 2007, "Rattlesnake Hills - WA", 0, 0, "", "Aromas grab you and pull you into the glass with blueberry pie, smoke, vanilla, and licorice. The palate is tart with black and blue fruit flavors. Quite dry with a lot of tannic structure. 100% Syrah. Verhey Vineyards. Aged 24 months in Rhone-style 100 gallon casks. 15.7% alcohol.", 4.0, "", "", 0.8427532540723319, 0.21022867624258285, 0.3010117183469687, 0.4698670239613827, 0.0, 0.0, 0.0, 0.0, 0.09798157946306094, 0.19604960054893886, 0.27508526959417706, 0.0, 0.3333333333333333, 0.31785688000637347, 0.3333333333333333, 0.0);
