INSERT INTO Wines VALUES(821, "Premier M??????lange", "Bordeaux-style Blend", "Lantz Cellars", "Red", 2007, "Columbia Valley - WA", 0, 0, "", "An alluring aroma profile with dark fruit, blueberry pie, spice, vanilla, and light wood accents. The palate is tight and compact with a big ball of fruit at the core along with dry, chalky tannins. A very well made wine. Oak occasionally comes a bit too far to the fore. 72% Merlot (Buoy Vineyards), 22% Cabernet Sauvignon (Lawrence Vineyards), and 6% Petit Verdot (Gilbert Vineyards). Aged 18 months in thin stave French oak. 15.7% alcohol.", 3.0, "", "", 0.6408825254373985, 0.1264880058615406, 0.07380972812569468, 0.31107773879213857, 0.0, 0.0, 0.0, 0.0, 0.06486917921091714, 0.35897219458183793, 0.3540721024202237, 0.0, 0.2707508968567816, 0.2104386867975509, 0.465231979735249, 0.0);
