INSERT INTO Wines VALUES(541, "Viognier", "Viognier", "McKinley Springs", "White", 2008, "Horse Heaven Hills - WA", 0, 0, "", "Almost completely clear in color. A lightly aromatic nose with floral and mineral notes along with white peaches. Crisp and clean on the palate with an acidic finish. Alcohol seems a bit heavy on the palate at times. Needs food to fully express itself. Aged in stainless steel sur lies. 14.6% alcohol. 225 cases produced.", 3.0, "", "", 0.0, 0.10621979298036306, 0.43417533860334373, 0.0, 0.0, 0.904846148666005, 0.3315178654977717, 0.0, 0.4142443442854965, 0.0, 0.13898912798425497, 0.0, 0.0, 0.0, 0.1263145861911294, 0.12955342173449166);
