INSERT INTO Wines VALUES(531, "Armada Vineyard Syrah Bottle Sample", "Syrah", "Cayuse Vineyards", "Red", 2007, "Walla Walla Valley - WA", 0, 0, "", "Holy smokes! Full on black pepper along with violets and game on a nose that is significantly more evolved due to the extra year of bottle age. A persistent intensity on the palate with crazy game tastes. Rich without being fruity. 100% Syrah. 14.2% alcohol. To be released in 2010.", 5.0, "", "", 0.0, 0.3065234071186722, 0.0, 0.0, 0.0, 0.7281795014840976, 0.0, 0.0, 0.21651207350795826, 0.21660757949539006, 0.3633527995153996, 0.0, 0.0, 0.0, 0.22733767718335618, 0.0);
