INSERT INTO Wines VALUES(163, "Merlot", "Merlot", "Leonetti Cellar", "Red", 2007, "Walla Walla Valley - WA", 0, 0, "", "Abundant sweet oak aromas along with pain grill??????, violets, orange peel, and smoke. A thick chewy taste yet surprisingly soft tannins. A full mid-palate and a pleasing finish round it out. Give 3-5 years and do not spill on porch when opening. 93% Merlot; 7% Petit Verdot. Seven Hills, Loess, Mill Creek Upland, and Pepper Bridge vineyards. 2,389 cases produced.", 5.0, "", "", 0.27659994147546846, 0.3532745350064546, 0.23227451566765472, 0.0, 0.0, 0.6319320336245523, 0.0, 0.0, 0.20413917739020618, 0.20422922554961956, 0.21774124837424777, 0.0, 0.0, 0.0, 0.2143461362597165, 0.0);
