INSERT INTO Wines VALUES(159, "C?????????est Syrah Magnifique", "Syrah", "Walter Dacon Wines", "Red", 2006, "Columbia Valley - WA", 0, 0, "", "An engaging nose with light smoke, white pepper, floral, and meat aromas. Perfectly balanced. An elegant wine with an extended finish. One of the top wines of the event. 32% Boushey, 32% Elephant Mountain, 28% Ranch at the End of the Road, and 8% Destiny Ridge vineyards. 14.8% alcohol. 294 cases produced.", 5.0, "", "", 0.12217584250940187, 0.0, 0.0, 0.36783549147907163, 0.0, 0.0, 0.21138094405345006, 0.0, 0.2648295311887615, 0.1394746062159976, 0.0, 0.0, 0.0, 0.24883432059441138, 0.19247429992926954, 0.0);
